mapper.select:
select ID, CODE, NAME, CHNL_TYPE_ID, STATUS, WEIGHT, CREATOR_ID, CREATE_DATE, LAST_MODIFIER_ID, LAST_MODIFY_TIME
from P_CHNL_GROUP

	and ID = #{id}

select ID, CODE, NAME, CHNL_TYPE_ID, STATUS, WEIGHT, CREATOR_ID, CREATE_DATE, LAST_MODIFIER_ID, LAST_MODIFY_TIME
from P_CHNL_GROUP


		and ID = #{id}


		and CODE = #{code}


		and NAME = #{name}


		and CHNL_TYPE_ID = #{chnlTypeId}


		and STATUS = #{status}


		and WEIGHT = #{weight}


		and CREATOR_ID = #{creatorId}


		and CREATE_DATE = #{createDate}


		and LAST_MODIFIER_ID = #{lastModifierId}


		and LAST_MODIFY_TIME = #{lastModifyTime}

mapper.insert:
INSERT INTO P_CHNL_GROUP
	( CODE, NAME, CHNL_TYPE_ID, STATUS, WEIGHT, CREATOR_ID, CREATE_DATE, LAST_MODIFIER_ID, LAST_MODIFY_TIME)
	VALUES(#{code},#{name},#{chnlTypeId},#{status},#{weight},#{creatorId},#{createDate},#{lastModifierId},#{lastModifyTime});
