mapper.select:
	select ID,CODE,NAME,MPED_INDEX,CP_ID,MP_ID,METER_ID,MAIN_FLAG,M_MPED_ID,P_MPED_ID,E_MPED_ID,VOLT_CODE,CONS_ID,TG_ID,LINE_ID,SUBS_ID,TRAN_ID,CAP_ID,BUSINESS_PLACE_CODE,MPED_PROP,MP_TYPE,SIDE_CODE,SWITCH_ID,SWITCH_NO,METER_BOX_ID,MEAS_MODE,WIRING_MODE,STATUS_CODE,VOLT_TYPE,DATAINTERVAL,INCREMENT_FLAG,CALC_TPL_ID,CALC_SCHEME,CHK_TPL_ID,Q_CALC_FLAG,MP_CALC_MODE,GUID,DATA_SRC,DATA_ID,AREA_CODE,WEIGHT,MP_CALC_TYPE,SETTLE_MPED_ID,METER_CLASS,CREATOR_ID,CREATE_DATE,LAST_MODIFIER_ID,LAST_MODIFY_TIME
from P_MPED


		and ID = #{id}


		and CODE = #{code}


		and NAME = #{name}


		and MPED_INDEX = #{mpedIndex}


		and CP_ID = #{cpId}


		and MP_ID = #{mpId}


		and METER_ID = #{meterId}


		and MAIN_FLAG = #{mainFlag}


		and M_MPED_ID = #{mMpedId}


		and P_MPED_ID = #{pMpedId}


		and E_MPED_ID = #{eMpedId}


		and VOLT_CODE = #{voltCode}


		and CONS_ID = #{consId}


		and TG_ID = #{tgId}


		and LINE_ID = #{lineId}


		and SUBS_ID = #{subsId}


		and TRAN_ID = #{tranId}


		and CAP_ID = #{capId}


		and BUSINESS_PLACE_CODE = #{businessPlaceCode}


		and MPED_PROP = #{mpedProp}


		and MP_TYPE = #{mpType}


		and SIDE_CODE = #{sideCode}


		and SWITCH_ID = #{switchId}


		and SWITCH_NO = #{switchNo}


		and METER_BOX_ID = #{meterBoxId}


		and MEAS_MODE = #{measMode}


		and WIRING_MODE = #{wiringMode}


		and STATUS_CODE = #{statusCode}


		and VOLT_TYPE = #{voltType}


		and DATAINTERVAL = #{datainterval}


		and INCREMENT_FLAG = #{incrementFlag}


		and CALC_TPL_ID = #{calcTplId}


		and CALC_SCHEME = #{calcScheme}


		and CHK_TPL_ID = #{chkTplId}


		and Q_CALC_FLAG = #{qCalcFlag}


		and MP_CALC_MODE = #{mpCalcMode}


		and GUID = #{guid}


		and DATA_SRC = #{dataSrc}


		and DATA_ID = #{dataId}


		and AREA_CODE = #{areaCode}


		and WEIGHT = #{weight}


		and MP_CALC_TYPE = #{mpCalcType}


		and SETTLE_MPED_ID = #{settleMpedId}


		and METER_CLASS = #{meterClass}


		and CREATOR_ID = #{creatorId}


		and CREATE_DATE = #{createDate}


		and LAST_MODIFIER_ID = #{lastModifierId}


		and LAST_MODIFY_TIME = #{lastModifyTime}
