mapper.select:
	select ID,CODE,NAME,CP_TYPE_CODE,STATUS_CODE,CP_ADDR,GPS_LONGITUDE,GPS_LATITUDE,BUSINESS_PLACE_CODE,TPL_ID,TERMINAL_ID,RELA_OBJ_TYPE,RELA_OBJ_ID,C_FLAG,TERMINAL_ADDR,PROTOCOL_ID,CTRL_HOSTID,MASTER_CHNL_GROUP,MASTER_IP_PORT,MASTER_TEL,SPARE_CHNL_GROUP,SPARE_IP_PORT,SPARE_TEL,GPRS_CODE,HEARTBEAT_CYCLE,ALG_NO,ALG_KEY,AREA_CODE,MASTERID,GUID,CREATOR_ID,CREATE_DATE,LAST_MODIFIER_ID,LAST_MODIFY_TIME
from R_CP


		and ID = #{id}


		and CODE = #{code}


		and NAME = #{name}


		and CP_TYPE_CODE = #{cpTypeCode}


		and STATUS_CODE = #{statusCode}


		and CP_ADDR = #{cpAddr}


		and GPS_LONGITUDE = #{gpsLongitude}


		and GPS_LATITUDE = #{gpsLatitude}


		and BUSINESS_PLACE_CODE = #{businessPlaceCode}


		and TPL_ID = #{tplId}


		and TERMINAL_ID = #{terminalId}


		and RELA_OBJ_TYPE = #{relaObjType}


		and RELA_OBJ_ID = #{relaObjId}


		and C_FLAG = #{cFlag}


		and TERMINAL_ADDR = #{terminalAddr}


		and PROTOCOL_ID = #{protocolId}


		and CTRL_HOSTID = #{ctrlHostid}


		and MASTER_CHNL_GROUP = #{masterChnlGroup}


		and MASTER_IP_PORT = #{masterIpPort}


		and MASTER_TEL = #{masterTel}


		and SPARE_CHNL_GROUP = #{spareChnlGroup}


		and SPARE_IP_PORT = #{spareIpPort}


		and SPARE_TEL = #{spareTel}


		and GPRS_CODE = #{gprsCode}


		and HEARTBEAT_CYCLE = #{heartbeatCycle}


		and ALG_NO = #{algNo}


		and ALG_KEY = #{algKey}


		and AREA_CODE = #{areaCode}


		and MASTERID = #{masterid}


		and GUID = #{guid}


		and CREATOR_ID = #{creatorId}


		and CREATE_DATE = #{createDate}


		and LAST_MODIFIER_ID = #{lastModifierId}


		and LAST_MODIFY_TIME = #{lastModifyTime}
